// Export helper functions
export function getActorById(id: string) {
  return actorFixtures.find(a => a.id === id);
}

export function getActorsByType(type: string) {
  return actorFixtures.filter(a => a.type === type);
}

export function getActorsByOrigin(origin: string) {
  return actorFixtures.filter(a => a.origin === origin);
}

export function getActiveActors() {
  return actorFixtures.filter(a => a.active === true);
}

export function getNationStateActors() {
  return actorFixtures.filter(a => a.type === 'nation-state');
}

export function getCybercriminalActors() {
  return actorFixtures.filter(a => a.type === 'cybercriminal');
}
