// Export helper functions
export function getCaseById(id: string) {
  return caseFixtures.find(c => c.id === id);
}

export function getCasesByStatus(status: string) {
  return caseFixtures.filter(c => c.status === status);
}

export function getCasesByPriority(priority: string) {
  return caseFixtures.filter(c => c.priority === priority);
}

export function getActiveCases() {
  return caseFixtures.filter(c => c.status === 'investigating' || c.status === 'open');
}

export function getCriticalCases() {
  return caseFixtures.filter(c => c.priority === 'critical');
}
