// Export helper functions
export function getUserById(id: string) {
  return userFixtures.find(u => u.id === id);
}

export function getUserByUsername(username: string) {
  return userFixtures.find(u => u.username === username);
}

export function getUsersByRole(role: string) {
  return userFixtures.filter(u => u.role === role);
}

export function getActiveUsers() {
  return userFixtures.filter(u => u.active === true);
}

export function getAdminUsers() {
  return userFixtures.filter(u => u.role === 'admin' && u.active);
}

export function getAnalystUsers() {
  return userFixtures.filter(u => u.role === 'analyst' && u.active);
}

export function getInvestigatorUsers() {
  return userFixtures.filter(u => u.role === 'investigator' && u.active);
}

export function getViewerUsers() {
  return userFixtures.filter(u => u.role === 'viewer' && u.active);
}
